.section .text
.global _start

/* print_dot x0 with length x1*/
print_dot:  
  mov x0, #1     
  ldr x1, =dot
  ldr x2, =len_dot
  mov w8, #64 
  svc #0
  ret

/* Check if x0 is a start-of-packet marker */
is_marker:
  mov x6, #1   // ret value: x6=1 true, x6=0 false
  mov x10, x1
  eor x1, x1, x1
  loop1:
    cmp x1, x10
    beq end_loop1
    ldrb w3, [x0, x1] 
    add x2, x1, #1 

    loop2:
      cmp x2, x10
      beq end_loop2
      ldrb w4, [x0, x2] 
      cmp w3, w4
      beq fail
      add x2, x2, #1
      b loop2

    end_loop2:
    add x1, x1, #1
    b loop1

  fail:
  mov x6, #0

  end_loop1:
  mov x0, x6
  ret


_start:
  ldr x15, =input
  mov x16, #14  // Modify here to change the size of the packet

loop3:
  mov x0, x15
  mov x1, x16
  bl is_marker
  cmp x0, #1 
  beq end_loop3
  bl print_dot
  add x15, x15, #1
  b loop3

end_loop3:  
  mov x5, #0

loop4:
  cmp x5, x16
  beq end_loop4
  bl print_dot
  add x5, x5, #1
  b loop4

end_loop4:
  mov x0, #0
  ldr x1, =nl
  ldr x2, =len_nl 
  mov w8, #64 
  svc #0
  mov x0, #0 
  mov w8, #93 
  svc #0


input:
.ascii ""
len_input = . - input

dot:
.ascii "."
len_dot = . - dot

nl:
.ascii "\n"
len_nl = . - nl
