.section .text
.global _start

/* print_dot x0 with length x1*/
print_dot:  
  mov x0, #1     
  ldr x1, =dot
  ldr x2, =len_dot
  mov w8, #64 
  svc #0
  ret

/* Check if x0 is a start-of-packet marker */
is_marker:
  mov x6, #1   // ret value: x6=1 true, x6=0 false
  ldrb w1, [x0], #1
  ldrb w2, [x0], #1
  ldrb w3, [x0], #1
  ldrb w4, [x0], #1

  cmp w1, w2
  beq is_marker_no
  cmp w1, w3
  beq is_marker_no
  cmp w1, w4
  beq is_marker_no
  cmp w2, w3
  beq is_marker_no
  cmp w2, w4
  beq is_marker_no
  cmp w3, w4
  beq is_marker_no
  bne is_marker_end

is_marker_no:
  mov x6, #0

is_marker_end:
  mov x0, x6
  ret


_start:
  ldr x10, =input
  mov x11, #0

loop:
  add x0, x10, x11
  bl is_marker
  cmp x0, #1 
  beq end
  add x11, x11, #1
  bl print_dot
  b loop

end:  
  bl print_dot
  bl print_dot
  bl print_dot
  bl print_dot
  mov x0, #0
  ldr x1, =nl
  ldr x2, =len_nl 
  mov w8, #64 
  svc #0
  mov x0, #0 
  mov w8, #93 
  svc #0

input:
.ascii ""
len_input = . - input

dot:
.ascii "."
len_dot = . - dot

nl:
.ascii "\n"
len_nl = . - nl
